import React from "react";


function ToTopButton(props) {



  console.log('component did mount') // does log
 let body = document.querySelector('body')
 body.addEventListener('scroll', function (e) {
   let windowScroll = window.scrollY
   console.log('scrollTop: ', body.scrollTop) // always logs 0
   console.log('scrollY: ', window.scrollY) // always logs 0
   console.log('pageYOffset: ', window.pageYOffset) // always logs 0
 })


window.scroll(function () {


  	((window.pageYOffset || document.documentElement.scrollTop) > 100) ? document.getElementByClassName("back-to-top").css({ opacity: 1, visibility: "visible" }) : document.getElementByClassName("back-to-top").css({ opacity: 0, visibility: "hidden" });
  });

function scrollfn(e) {
	let $target = document.querySelector(e),
	offSet = e === "#overview" ? 0 : $target.offset().top;
	document.querySelector('html, body').animate({
		'scrollTop': offSet
	}, 1200, 'swing');


}

  return (

    <div className="back-to-top"
     onClick={scrollfn("#overview")}>
      <i aria-hidden="true" class="far fa-angle-up"></i>
    </div>





  )
};




export default ToTopButton;
